var mqtt = require('mqtt');
var client = mqtt.connect('mqtt://broker.hivemq.com:1883');

var nano = require('nano')('http://localhost:5984');
var test_db = nano.db.use('ranked');

client.subscribe('interactivity');                                        
client.handleMessage = function(packet, done){

	var doc = packet.payload.toString();

	var obj = JSON.parse(doc);

console.log(obj);

	var data = { School: obj.School,Location:
obj.location,interactive: obj.interactive,noninteractive: obj.noninteractive};
	
console.log(data);


	test_db.insert(data);

        console.log(packet.payload.toString());

        done();

}
