var fs=require('fs').readFileSync('audiotest').toString().split('\n').forEach(function(line){
var Meyda =require('meyda');
Meyda.bufferSize=64;
var data =new Float32Array(JSON.parse(line));
if(data){
	var out= Meyda.extract('rms',data)+','+Meyda.extract('energy',data)+','+Meyda.extract('spectralCentroid',data)+','+Meyda.extract('spectralSlope',data)+','+Meyda.extract('perceptualSpread',data)+','+'state'+'\n';
	var fs1=require('fs');
	fs1.appendFileSync('test.arff',out);
	}
});


