
function server(){

var mqtt = require('mqtt');
var client = mqtt.connect('mqtt://broker.hivemq.com:1883');
var sleep =require('sleep');
var i=0;
var out1=[{'0':'','1':''}];
var fs=require('fs').readFileSync('Reads').toString().split('\n').forEach(function(line){
	out1[i]=line;
	i=i+1;
});
var out='{"school":"AUS","location":"EB2036","interactive":'+out1[0]+',"noninteractive":'+out1[1]+'}';
console.log(out);
client.publish('interactivity',out);							
sleep.sleep(36000);
}
var infiniteloop=require('infinite-loop');
var loop=new infiniteloop();
loop.add(server);
loop.run();